(()=>{"use strict";document.getElementById("searchButton").addEventListener("click",(async function(){try{const e=document.getElementById("search").value,t=document.getElementById("mainBody"),a=await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${e}&units=metric&cnt=5&appid=f36cabee0bdc20db9a756fb2ce2b4d57`),n=await a.json();t.reset();const i={location:n.name,temperature:n.main.temp,min:Math.round(n.main.temp_min),max:Math.round(n.main.temp_max),weather:n.weather[0].description,lat:n.coord.lat,lon:n.coord.lon},d=await fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=${i.lat}&lon=${i.lon}&exclude=hourly,minutely&units=metric&appid=f36cabee0bdc20db9a756fb2ce2b4d57`),o=await d.json(),c=document.getElementById("content");c.innerHTML="";const r=document.getElementById("content2");r.innerHTML="";const m=document.createElement("div");m.className="dataTab",m.id=i.location,m.innerHTML=`<div><h1>${i.location}</h1><a>TODAY</a></div> \n      <div><h2>${i.temperature}°C</h2></div> \n      <div>L:${i.min}°C -- H:${i.max}°C</div> \n      <div class="descriptionWeather">${i.weather.toUpperCase()}</div>`,c.append(m);for(let e=1;e<8;e+=1){const t={location:o.timezone,temperature:o.daily[e].temp.day,min:Math.round(o.daily[e].temp.min),max:Math.round(o.daily[e].temp.max),weather:o.daily[e].weather[0].description,date:new Date(1e3*o.daily[e].dt).toLocaleDateString("en-us",{weekday:"long",year:"numeric",month:"short",day:"numeric"})},a=document.createElement("div");a.className="forecast",a.id=i.location,a.innerHTML=`<div><h1>${i.location}</h1><a>${t.date}</a></div> \n      <div><h2>${t.temperature}°C</h2></div> \n      <div>L:${t.min}°C -- H:${t.max}°C</div> \n      <div class="descriptionWeather">${t.weather.toUpperCase()}</div>`,r.append(a)}}catch(e){console.log(e)}}))})();